import React, { useContext, useState } from "react"
import { useEffect } from "react"
import { useHistory } from "react-router-dom"
import { UserContext } from "../UserContext"
import Input from "../components/Input"
import SelectInput from "../components/SelectInput"
import FormKit from "../data/FormKit"
import UserKit from "../data/UserKit"

// when the form is rendered on the page, it is rendered with up to three props.
// * a type that matches an array i FormKit.js, the form will loop over the array and print out
//   the corresponding inputs.
// * customerEdit, if customerEdit is sent with the props, the form renders the Inputs from formKit.customer
//   and sets the values to the input from customerEdit
const TheForm = ({ type, customerEdit, submit }) => {
  const userKit = new UserKit()
  const formKit = new FormKit()
  const history = useHistory()
  const [fieldValues, setFieldValues] = useState()

  const { setCurrentUser } = useContext(UserContext)

  const { triggerListUpdate, setTriggerListUpdate } = useContext(UserContext)

  // depending on what type of form is requested
  // initialFieldValues is set by the values collected from FormKit.js
  useEffect(() => {
    let form = formKit[type]
    let initialFieldState = form.reduce((acc, cur) => {
      return {
        ...acc,
        [cur.name]: cur.options ? cur.options[0] : "",
      }
    }, {})
    setFieldValues(initialFieldState)
  }, [])

  useEffect(() => {
    customerEdit && setFieldValues(customerEdit)
  }, [])

  const handleChange = (name, value) => {
    setFieldValues((prevState) => {
      return {
        ...prevState,
        [name]: value,
      }
    })
  }

  const handleSubmit = (e) => {
    e.preventDefault()

    switch (submit) {
      case "register":
        userKit.register(fieldValues)
        break
      case "login":
        userKit
          .login(fieldValues)
          .then((res) => res.json())
          .then((data) => {
            if (data.token) {
              setCurrentUser(true)
              userKit.setSessionToken(data.token)
              history.push("/")
            }
          })
        break
      case "addCustomer":
        userKit.addCustomer(fieldValues)
        break
      case "editCustomer":
        userKit
          .updateCustomer(customerEdit.id, fieldValues)
          .then(history.push("/"))
        break
      default:
        console.log("default")
    }
    setTriggerListUpdate(!triggerListUpdate)
  }

  return (
    <form>
      {fieldValues &&
        formKit[type].map((field) => {
          if (field.type === "input") {
            return (
              <Input
                name={field.name}
                label={field.name}
                value={fieldValues[field.name]}
                handleChange={handleChange}
              ></Input>
            )
          }
          if (field.type === "select") {
            return (
              <SelectInput
                name={field.name}
                label={field.name}
                value={fieldValues[field.name]}
                handleChange={handleChange}
                options={field.options}
              ></SelectInput>
            )
          }
        })}
      <button onClick={(e) => handleSubmit(e)}>
        {customerEdit ? "update" : type}
      </button>
    </form>
  )
}

export default TheForm
