import React, { useState } from "react"
import { useHistory } from "react-router-dom"
import SignUpLoginForm from "../containers/SignUpLoginForm"
import UserKit from "../data/UserKit"

const SignUpLogin = () => {
  const [alreadyMember, setAlreadyMember] = useState(false)
  const history = useHistory()
  const userkit = new UserKit()
  const searchString = history.location.search

  const urlParameters = new URLSearchParams(searchString)
  let uid = urlParameters.get("uid")
  let token = urlParameters.get("token")

  const handleActivate = () => {
    userkit.activateUser(uid, token)
  }
  const form = () => {
    if (alreadyMember) {
      return <SignUpLoginForm type="login" />
    } else {
      return (
        <div>
          <SignUpLoginForm type="register" />{" "}
          <button onClick={() => setAlreadyMember(true)}>
            already a member?
          </button>
        </div>
      )
    }
  }
  return (
    <div>
      {uid && token ? (
        <button onClick={handleActivate}>Activate User</button>
      ) : (
        form()
      )}
    </div>
  )
}

export default SignUpLogin
