const ROOT_URL = "https://frebi.willandskill.eu/"

export default class {
  async login(user) {
    const url = `${ROOT_URL}api-token-auth/`
    const payload = user
    return fetch(url, {
      method: "POST",
      headers: this.getPublicHeaders(),
      body: JSON.stringify(payload),
    })
  }
  async activateUser(uid, token) {
    const url = `${ROOT_URL}auth/users/activate`
    const payload = { uid, token }
    return fetch(url, {
      method: "POST",
      headers: this.getPublicHeaders(),
      body: JSON.stringify(payload),
    })
  }
  async register(user) {
    const url = `${ROOT_URL}auth/users/`
    const payload = user

    return fetch(url, {
      method: "POST",
      headers: this.getPublicHeaders(),
      body: JSON.stringify(payload),
    })
  }

  getPublicHeaders() {
    return {
      "Content-Type": "application/json",
    }
  }

  setSessionToken(token) {
    sessionStorage.setItem("sessionToken", JSON.stringify(token))
  }
  getSessionToken() {
    return sessionStorage.getItem()
  }
}
