import React, { useState } from "react"
import { useEffect } from "react"
import Input from "../components/Input"
import SelectInput from "../components/SelectInput"
import FormKit from "../data/FormKit"
import UserKit from "../data/UserKit"

const SignUpLoginForm = ({ type }) => {
  const userKit = new UserKit()
  const formKit = new FormKit()
  const [user, setUser] = useState()

  useEffect(() => {
    let loginForm = formKit[type]()
    let initialUserState = loginForm.reduce((acc, cur) => {
      return {
        ...acc,
        [cur.name]: cur.options ? cur.options[0] : "",
      }
    }, {})
    setUser(initialUserState)
  }, [])

  const handleChange = (name, value) => {
    setUser((prevState) => {
      return {
        ...prevState,
        [name]: value,
      }
    })
  }

  const handleSubmit = (e) => {
    e.preventDefault()
    type === "register" && userKit.register(user)
    type === "login" &&
      userKit
        .login(user)
        .then((res) => res.json())
        .then((data) => {
          console.log(data)
        })
  }

  return (
    <form>
      {user &&
        formKit[type]().map((field) => {
          if (field.type === "input") {
            return (
              <Input
                name={field.name}
                label={field.name}
                value={user[field.name]}
                handleChange={handleChange}
              ></Input>
            )
          }
          if (field.type === "select") {
            return (
              <SelectInput
                name={field.name}
                label={field.name}
                value={user[field.name]}
                handleChange={handleChange}
                options={field.options}
              ></SelectInput>
            )
          }
        })}
      <button onClick={(e) => handleSubmit(e)}>{type}</button>
    </form>
  )

  // return (
  //   <form>
  //     {type === "register" && (
  //       <div>
  //         <Input
  //           handleChange={handleChange}
  //           label="first name"
  //           name="firstName"
  //           value={user}
  //         ></Input>
  //         <Input
  //           handleChange={handleChange}
  //           label="last name"
  //           name="lastName"
  //           value={user}
  //         ></Input>
  //       </div>
  //     )}
  //     <Input
  //       handleChange={handleChange}
  //       label="email"
  //       name="email"
  //       value={user}
  //     ></Input>
  //     <Input
  //       handleChange={handleChange}
  //       label="password"
  //       name="password"
  //       value={user}
  //     ></Input>
  //     {type === "register" && (
  //       <div>
  //         <Input
  //           handleChange={handleChange}
  //           label="organisation name"
  //           name="organisationName"
  //           value={user}
  //         ></Input>
  //         <SelectInput
  //           handleChange={handleChange}
  //           label="organisation kind"
  //           name="organisationKind"
  //           type="select"
  //           value={user}
  //         ></SelectInput>
  //       </div>
  //     )}
  //     <button onClick={(e) => handleSubmit(e)}>{type}</button>
  //   </form>
  // )
}

export default SignUpLoginForm
