export default class {
  login = [
    { name: "email", input: "input", required: true, type: "input" },
    { name: "password", input: "input", required: true, type: "password" },
  ]

  register = [
    { name: "firstName", input: "input" },
    { name: "lastName", input: "input" },
    { name: "email", input: "input" },
    { name: "password", input: "input" },
    { name: "organisationName", input: "input" },
    { name: "organisationKind", input: "select", options: [1, 2, 3] },
  ]

  customer = [
    { name: "name", input: "input" },
    { name: "organisationNr", input: "input" },
    { name: "vatNr", input: "input" },
    { name: "reference", input: "input" },
    { name: "paymentTerm", input: "input" },
    { name: "website", input: "input" },
    { name: "email", input: "input" },
    { name: "phoneNumber", input: "input" },
  ]
}

