import React, { useContext, useEffect, useState } from "react"
import { useHistory } from "react-router-dom"

import UserKit from "../data/UserKit"
import CustomerList from "../components/CustomerList"
import TheForm from "../containers/TheForm"
import { UserContext } from "../UserContext"
import styled from "styled-components"
import { StyledButton } from "./SignUpLogin"

// homepage checks if user has token, if not, user will be redirected to login/sign-up page
// if add button is pressed, the form with "Customer"-settings will render on page
const Home = () => {
  const userKit = new UserKit()
  const history = useHistory()
  const [addCustomer, setAddCustomer] = useState(false)
  const { currentUser, setCurrentUser } = useContext(UserContext)

  useEffect(() => {
    const token = userKit.getSessionToken()
    !token && history.push("/login")
  }, [])

  useEffect(() => {
    userKit.getSessionToken() &&
      userKit
        .getCurrentUser()
        .then((res) => res.json())
        .then((data) => setCurrentUser(data))
  })

  const handleClick = () => {
    setAddCustomer(!addCustomer)
  }
  return (
    <div>
      <CustomerList />
      <StyledButton onClick={handleClick}>
        {!addCustomer ? "add" : "back"}
      </StyledButton>
      <StyledFormWrapper>
        {addCustomer && <TheForm submit="Add customer" type="customer" />}
      </StyledFormWrapper>
    </div>
  )
}

export default Home

export const StyledFormWrapper = styled.div`
  width: 400px;
`
