import React, { useContext, useState } from "react"
import { useEffect } from "react"
import { AuthContext } from "../AuthContext"
import Input from "../components/Input"
import SelectInput from "../components/SelectInput"
import FormKit from "../data/FormKit"
import UserKit from "../data/UserKit"

const SignUpLoginForm = ({ type }) => {
  const userKit = new UserKit()
  const formKit = new FormKit()
  const history = useHistory()
  const [user, setUser] = useState()
  const { setCurrentUser } = useContext(AuthContext)

  // depending on what type of form is requested
  // initalUserState is set by the values collected from FormKit.js
  useEffect(() => {
    let form = formKit[type]()
    let initialUserState = form.reduce((acc, cur) => {
      return {
        ...acc,
        [cur.name]: cur.options ? cur.options[0] : "",
      }
    }, {})
    setUser(initialUserState)
  }, [])

  const handleChange = (name, value) => {
    setUser((prevState) => {
      return {
        ...prevState,
        [name]: value,
      }
    })
  }

  const handleSubmit = (e) => {
    e.preventDefault()
    type === "register" && userKit.register(user)
    type === "login" &&
      userKit
        .login(user)
        .then((res) => res.json())
        .then((data) => {
          console.log(data)
          setCurrentUser(true)
          history.pushState
        })
  }

  return (
    <form>
      {user &&
        formKit[type]().map((field) => {
          if (field.type === "input") {
            return (
              <Input
                name={field.name}
                label={field.name}
                value={user[field.name]}
                handleChange={handleChange}
              ></Input>
            )
          }
          if (field.type === "select") {
            return (
              <SelectInput
                name={field.name}
                label={field.name}
                value={user[field.name]}
                handleChange={handleChange}
                options={field.options}
              ></SelectInput>
            )
          }
        })}
      <button onClick={(e) => handleSubmit(e)}>{type}</button>
    </form>
  )
}

export default SignUpLoginForm
