import React, { useState, useEffect, createContext } from "react"
import UserKit from "./data/UserKit"
import { updateCustomerList } from "./utils"

export const UserContext = createContext()

// auth wraps the entire app giving currentUser and customers as context
const User = ({ children }) => {
  const userKit = new UserKit()
  const [currentUser, setCurrentUser] = useState(null)
  const [customerList, setCustomerList] = useState(null)

  useEffect(() => {
    userKit.getSessionToken() &&
      userKit
        .getCurrentUser()
        .then((res) => res.json())
        .then((data) => setCurrentUser(data))
  }, [])

  useEffect(() => {
    updateCustomerList(userKit, setCustomerList)
  }, [])

  return (
    <UserContext.Provider
      value={{
        currentUser,
        setCurrentUser,
        customerList,
        setCustomerList,
      }}
    >
      {children}
    </UserContext.Provider>
  )
}
export default User
