import React from "react"

const SelectInput = ({ label, name, handleChange, options }) => {
  console.log(options)
  return (
    <div>
      <label>{label}</label>

      <select
        onChange={(e) => handleChange(e.target.name, e.target.value)}
        name={name}
      >
        {options.map((option) => {
          return <option value={option}>{option}</option>
        })}
      </select>
    </div>
  )
}

export default SelectInput
