import React from "react"

const Input = ({ label, name, setUser }) => {
  const handleChange = (name, value) => {
    setUser((prevState) => {
      return {
        ...prevState,
        [name]: value,
      }
    })
  }
  return (
    <div>
      <label>{label}</label>
      <input
        onChange={(e) => handleChange(e.target.name, e.target.value)}
        name={name}
      ></input>
    </div>
  )
}

export default Input
