export const updateCustomerList = (kit, setList) => {}

const handleSubmit = (e) => {
  e.preventDefault()

  switch (submit) {
    case "register":
      userKit.register(fieldValues)
      break
    case "login":
      userKit
        .login(fieldValues)
        .then((res) => res.json())
        .then((data) => {
          if (data.token) {
            setCurrentUser(true)
            userKit.setSessionToken(data.token)
            history.push("/")
          }
        })
      break
    case "addCustomer":
      userKit.addCustomer(fieldValues)
      break

    case "editCustomer":
      userKit
        .updateCustomer(customerEdit.id, fieldValues)
        .then(history.push("/"))
    default:
      console.log("default")
  }
}
