import React, { useContext, useEffect, useState } from "react"
import { useHistory } from "react-router-dom"
import UserKit from "../data/UserKit"
import { CustomerContext, UserContext } from "../UserContext"

const CustomerList = () => {
  const history = useHistory()
  const userKit = new UserKit()
  const { customerList } = useContext(CustomerContext)
  const { setCustomer } = useContext(UserContext)

  const handleClick = (customer) => {
    history.push(`/customer/${customer.id}`)
    setCustomer(customer)
  }
  return (
    <div>
      I am list
      {customerList &&
        customerList.map((customer) => {
          return (
            <div>
              <p onClick={() => handleClick(customer)}>name: {customer.name}</p>
            </div>
          )
        })}
    </div>
  )
}

export default CustomerList
