import React, { useState } from "react"
import Input from "../components/Input"
import UserKit from "../data/UserKit"

const SignUpLoginForm = ({ type }) => {
  const userKit = new UserKit()
  const [user, setUser] = useState({})

  console.log(user)
  setUser((prevState, name, value) => {
    return {
      ...prevState,
      [name]: value,
    }
  })

  const handleSubmit = (e) => {
    e.preventDefault()
    type === "register" && userKit.register()
    type === "login" && userKit.login()
  }

  return (
    <form>
      {type === "register" && (
        <div>
          <Input setUser={setUser} label="first name" name="firstName"></Input>
          <Input setUser={setUser} label="last name" name="lastName"></Input>
        </div>
      )}
      <Input setUser={setUser} label="email" name="email"></Input>
      <Input setUser={setUser} label="password" name="password"></Input>
      {type === "register" && (
        <div>
          <Input
            setUser={setUser}
            label="organisation name"
            name="organisationName"
          ></Input>
          <Input
            setUser={setUser}
            label="organisation kind"
            name="organisationKind"
            type="select"
          ></Input>
        </div>
      )}
      <button onClick={(e) => handleSubmit(e)}>{type}</button>
    </form>
  )
}

export default SignUpLoginForm
