import React, { useState } from "react"
import Input from "../components/Input"
import SelectInput from "../components/SelectInput"
import UserKit from "../data/UserKit"

const initialUserState = {
  firstName: "andy",
  lastName: "bandy",
  email: "hellberg.andreas+test2@gmail.com",
  password: "js-fend-19",
  organisationName: "aaaa",
  organisationKind: 1,
}

const SignUpLoginForm = ({ type }) => {
  const userKit = new UserKit()
  const [user, setUser] = useState({ ...initialUserState })

  const handleChange = (name, value) => {
    setUser((prevState) => {
      return {
        ...prevState,
        [name]: value,
      }
    })
  }

  const handleSubmit = (e) => {
    e.preventDefault()
    type === "register" && userKit.register(user)

    type === "login" &&
      userKit
        .login(user)
        .then((res) => res.json())
        .then((data) => {
          userKit.setToken(data.token)
        })
  }

  return (
    <form>
      {type === "register" && (
        <div>
          <Input
            handleChange={handleChange}
            label="first name"
            name="firstName"
            value={user}
          ></Input>
          <Input
            handleChange={handleChange}
            label="last name"
            name="lastName"
            value={user}
          ></Input>
        </div>
      )}
      <Input
        handleChange={handleChange}
        label="email"
        name="email"
        value={user}
      ></Input>
      <Input
        handleChange={handleChange}
        label="password"
        name="password"
        value={user}
      ></Input>
      {type === "register" && (
        <div>
          <Input
            handleChange={handleChange}
            label="organisation name"
            name="organisationName"
            value={user}
          ></Input>
          <SelectInput
            handleChange={handleChange}
            label="organisation kind"
            name="organisationKind"
            type="select"
            value={user}
          ></SelectInput>
        </div>
      )}
      <button onClick={(e) => handleSubmit(e)}>{type}</button>
    </form>
  )
}

export default SignUpLoginForm
