import React, { useContext, useEffect, useState } from "react"
import { useHistory } from "react-router-dom"
import TheForm from "../containers/TheForm"
import UserKit from "../data/UserKit"
import { UserContext } from "../UserContext"
import { updateCustomerList } from "../utils"

// with the id set in props, customerpage fetches the customer and renders it on the page
// the customer is saved to customer state

const CustomerPage = (props) => {
  const history = useHistory()
  const userKit = new UserKit()
  const [customer, setCustomer] = useState({})
  const [editCustomer, setEditCustomer] = useState(false)
  const { setCustomerList } = useContext(UserContext)

  useEffect(() => {
    const id = props.match.params.id
    userKit
      .getCustomer(id)
      .then((res) => res.json())
      .then((data) => setCustomer(data))
  }, [])

  const handleDelete = () => {
    userKit
      .deleteCustomer(customer.id)
      .then(() => updateCustomerList(userKit, setCustomerList))
    history.push("/home")
  }
  const handleEdit = () => {
    setEditCustomer(true)
  }

  return (
    <div>
      {customer && (
        <div>
          <p>{customer.name}</p>
          <p>{customer.email}</p>
          <p>{customer.website}</p>
        </div>
      )}
      <button onClick={handleEdit}>edit</button>
      <button onClick={handleDelete}>delete</button>
      {editCustomer && (
        <div>{<TheForm customerEdit={customer} type="addCustomer" />}</div>
      )}
    </div>
  )
}

export default CustomerPage
