import React, { useContext, useState } from "react"
import { useEffect } from "react"
import { useHistory } from "react-router-dom"
import { UserContext } from "../UserContext"
import Input from "../components/Input"
import SelectInput from "../components/SelectInput"
import FormKit from "../data/FormKit"
import UserKit from "../data/UserKit"
import { updateCustomerList } from "../utils"

const TheForm = ({ type, customerEdit }) => {
  const userKit = new UserKit()
  const formKit = new FormKit()
  const history = useHistory()
  const [fieldValues, setFieldValues] = useState()

  const { setCurrentUser } = useContext(UserContext)
  const { setCustomerList } = useContext(UserContext)

  // depending on what type of form is requested
  // initialFieldValues is set by the values collected from FormKit.js
  useEffect(() => {
    let form = formKit[type]
    let initialFieldState = form.reduce((acc, cur) => {
      return {
        ...acc,
        [cur.name]: cur.options ? cur.options[0] : "",
      }
    }, {})
    setFieldValues(initialFieldState)
  }, [])

  useEffect(() => {
    customerEdit && setFieldValues(customerEdit)
  }, [])

  const handleChange = (name, value) => {
    setFieldValues((prevState) => {
      return {
        ...prevState,
        [name]: value,
      }
    })
  }

  const handleSubmit = (e) => {
    e.preventDefault()

    if (customerEdit) {
      userKit
        .updateCustomer(customerEdit.id, fieldValues)
        .then(() => updateCustomerList(userKit, setCustomerList))
        .then(history.push("/"))
      formKit.editCustomer(userKit, customerEdit, fieldValues, setCustomerList)
    } else {
      switch (type) {
        case "register":
          console.log("registers")
          userKit.register(fieldValues)
          break

        case "login":
          console.log("logs in")
          userKit
            .login(fieldValues)
            .then((res) => res.json())
            .then((data) => {
              if (data.token) {
                setCurrentUser(true)
                userKit.setSessionToken(data.token)
                history.push("/")
              }
            })
          break
        case "addCustomer":
          console.log("adds customer")
          userKit
            .addCustomer(fieldValues)
            .then(() => updateCustomerList(userKit, setCustomerList))
          break
        default:
          console.log("default")
      }
    }
  }

  return (
    <form>
      {fieldValues &&
        formKit[type].map((field) => {
          if (field.type === "input") {
            return (
              <Input
                name={field.name}
                label={field.name}
                value={fieldValues[field.name]}
                handleChange={handleChange}
              ></Input>
            )
          }
          if (field.type === "select") {
            return (
              <SelectInput
                name={field.name}
                label={field.name}
                value={fieldValues[field.name]}
                handleChange={handleChange}
                options={field.options}
              ></SelectInput>
            )
          }
        })}
      <button onClick={(e) => handleSubmit(e)}>
        {customerEdit ? "update" : type}
      </button>
    </form>
  )
}

export default TheForm
