import React from "react"

const Input = ({ label, name, onChange, user }) => {
  const handleChange = (name, value) => {
    onChange(name, value)
  }
  return (
    <div>
      <label>{label}</label>
      <input
        onChange={(e) => handleChange(e.target.name, e.target.value)}
        name={name}
      ></input>
    </div>
  )
}

export default Input
