import React, { useContext, useState } from "react"
import { useEffect } from "react"
import { useHistory } from "react-router-dom"
import { UserContext } from "../UserContext"
import Input from "../components/Input"
import SelectInput from "../components/SelectInput"
import FormKit from "../data/FormKit"
import UserKit from "../data/UserKit"

const SignUpLoginForm = ({ type, customer }) => {
  const userKit = new UserKit()
  const formKit = new FormKit()
  const history = useHistory()
  const [fieldValues, setFieldValues] = useState()
  const { setCurrentUser } = useContext(UserContext)

  // depending on what type of form is requested
  // initalUserState is set by the values collected from FormKit.js
  useEffect(() => {
    let form = formKit[type]()
    let initialUserState = form.reduce((acc, cur) => {
      return {
        ...acc,
        [cur.name]: cur.options ? cur.options[0] : "",
      }
    }, {})
    setFieldValues(initialUserState)
  }, [])

  useEffect(() => {
    customer && setFieldValues(customer)
  }, [])
  const handleChange = (name, value) => {
    setFieldValues((prevState) => {
      return {
        ...prevState,
        [name]: value,
      }
    })
  }

  const handleSubmit = (e) => {
    e.preventDefault()
    type === "register" && userKit.register(fieldValues)
    type === "login" &&
      userKit
        .login(fieldValues)
        .then((res) => res.json())
        .then((data) => {
          if (data.token) {
            setCurrentUser(true)
            userKit.setSessionToken(data.token)
            history.push("/")
          }
        })
    type === "addCustomer" && userKit.addCustomer(fieldValues)
    customer && type === "addCustomer"
      ? userKit.updateCustomer(customer.id)
      : null
  }

  return (
    <form>
      {fieldValues &&
        formKit[type]().map((field) => {
          if (field.type === "input") {
            return (
              <Input
                name={field.name}
                label={field.name}
                value={fieldValues[field.name]}
                handleChange={handleChange}
              ></Input>
            )
          }
          if (field.type === "select") {
            return (
              <SelectInput
                name={field.name}
                label={field.name}
                value={fieldValues[field.name]}
                handleChange={handleChange}
                options={field.options}
              ></SelectInput>
            )
          }
        })}
      <button onClick={(e) => handleSubmit(e)}>{type}</button>
    </form>
  )
}

export default SignUpLoginForm
